vm=new Vue({el:"#root",data:()=>({navbar:!1,form:{name:"",phone:""}}),methods:{onFormSubmit(){this.form.name&&this.form.phone&&console.log(this.form)},setHeaderOnScroll(){const e=document.getElementById("header");e.children[0].style.opacity=(window.scrollY/80).toFixed(2),40<window.scrollY?e.classList.add("header_scroll"):e.classList.remove("header_scroll")}},watch:{navbar(e){const o=document.getElementById("body");e?o.style.overflow="hidden":o.removeAttribute("style")}},mounted(){window.addEventListener("scroll",this.setHeaderOnScroll)},beforeDestroy(){window.removeEventListener("scroll",this.setHeaderOnScroll)}});